lists.docs:
function(head, req) {
  var row;
  start({
    headers: {
      'Content-Type': 'text/html'
    }
  });
  send('<!DOCTYPE html><html lang=en>');
  send('<ol>');
  while(row = getRow()) {
    send('<li>' + row.key + '</li>');
  }
  send('</ol>');
}

updates.doc:
function(doc, req) {
  doc = doc || {};

  for (var field in req.form) {
    doc[field] = req.form[field];
  }
  doc.type = 'doc';

  return [doc, 'updated doc'];
}

docs.validate_doc_update:
function(newDoc, oldDoc, userCtx, secObj) {
  if (typeof newDoc.number !== 'number') {
    throw({ forbidden: 'Document must have a number.' });
  }
}
